import React, { Component } from "react";
import randomColor from "randomcolor";
import Counter from "./Counter";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      count: 0,
      color: "#FA6304",
      isMounted: true,
      subscriptionId: null
    };
  }

  updateCount = () =>
    this.setState({
      count: this.state.count + 1,
      color: randomColor()
    });

  updateMountStatus = () =>
    this.setState({ isMounted: !this.state.isMounted });

  subscribe = () => {
    const subscriptionId = setInterval(
      () => this.updateCount(),
      1500
    );
    this.setState({ subscriptionId });
  };

  cancelSubscription = () => {
    clearInterval(this.state.subscriptionId);
    this.setState({ subscriptionId: null, count: 0 });
  };

  render() {
    return this.state.isMounted ? (
      <div className="App">
        <Counter
          updateCount={this.updateCount}
          subscribe={this.subscribe}
          cancelSubscription={this.cancelSubscription}
          {...this.state}
        />
        <button onClick={this.updateMountStatus}>
          Unmount Component
        </button>
      </div>
    ) : (
      <div className="App">
        <button onClick={this.updateMountStatus}>
          Mount Component
        </button>
      </div>
    );
  }
}

export default App;
