import React, { Component } from "react";
import randomColor from "randomcolor";
import Counter from "./Counter";
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    console.log("constructor() invoked");

    this.state = {
      count: 90000,
      loading: true,
      color: "#FA6304",
      isMounted: true
    };
    console.log("state set");
  }

  initState = () => {
    var timer = setInterval(() => {
      const newCount = this.state.count - 1;
      this.setState({
        count: newCount,
        loading: false
      });
      if (this.state.count <= 0) {
        console.log('yeet');
        clearInterval(timer);
      }
    }, 1);
  }

  updateCount = () =>
    this.setState({
      count: this.state.count + 1,
      color: randomColor()
    });

  render() {
    // console.log("render has been executed!");
    return this.state.isMounted ? (
      <div className="App">
        <Counter
          initState={this.initState}
          updateCount={this.updateCount}
          clearParentState={this.clearParentState}
          {...this.state}
        />
        <button onClick={this.updateMountStatus}>
          Unmount Component
        </button>
      </div>
    ) : (
      <div className="App">
        <button onClick={this.updateMountStatus}>
          Mount Component
        </button>
      </div>
    );
  }
}

export default App;
